function sendData(){event.preventDefault();var a={};$.each($(this).serializeArray(),function(b,c){a[c.name]=c.value}),$.ajax({type:"POST",url:"/data",data:a,success:function(a){console.log("DB got the data"),getData()}})}function getData(){$.ajax({type:"GET",url:"/data",success:function(a){updateDom(a)}})}function updateDom(a){$("#messageContainer").empty();for(var b=0;b<a.length;b++){var c="<div class='well col-md-6'><p>"+a[b].name+"<p><p>"+a[b].message+"<p></div>";$("#messageContainer").append(c)}}$(document).ready(function(){$("#entryForm").submit(function(a){a.preventDefault();var b={};$.each($(this).serializeArray(),function(a,c){b[c.name]=c.value}),$.ajax({type:"POST",url:"/data",data:b,success:function(a){console.log("DB got the data"),getData()}})})});